<h3 id="delete-or-add-form-items-dynamically"><a class="header-anchor" href="#delete-or-add-form-items-dynamically" aria-hidden="true">¶</a> Delete or add form items dynamically</h3>
<demo-block class="demo-box" :jsfiddle="{&quot;html&quot;:&quot;&lt;el-form :model=\&quot;dynamicValidateForm\&quot; ref=\&quot;dynamicValidateForm\&quot; label-width=\&quot;120px\&quot; class=\&quot;demo-dynamic\&quot;&gt;\n  &lt;el-form-item prop=\&quot;email\&quot; label=\&quot;Email\&quot; :rules=\&quot;[\n      { required: true, message: 'Please input email address', trigger: 'blur' },\n      { type: 'email', message: 'Please input correct email address', trigger: 'blur,change' }\n    ]\&quot;&gt;\n    &lt;el-input v-model=\&quot;dynamicValidateForm.email\&quot;&gt;&lt;/el-input&gt;\n  &lt;/el-form-item&gt;\n  &lt;el-form-item v-for=\&quot;(domain, index) in dynamicValidateForm.domains\&quot; :label=\&quot;'Domain' + index\&quot; :key=\&quot;domain.key\&quot; :prop=\&quot;'domains.' + index + '.value'\&quot; :rules=\&quot;{\n      required: true, message: 'domain can not be null', trigger: 'blur'\n    }\&quot;&gt;\n    &lt;el-input v-model=\&quot;domain.value\&quot;&gt;&lt;/el-input&gt;&lt;el-button @click.prevent=\&quot;removeDomain(domain)\&quot;&gt;Delete&lt;/el-button&gt;\n  &lt;/el-form-item&gt;\n  &lt;el-form-item&gt;\n    &lt;el-button type=\&quot;primary\&quot; @click=\&quot;submitForm('dynamicValidateForm')\&quot;&gt;Submit&lt;/el-button&gt;\n    &lt;el-button @click=\&quot;addDomain\&quot;&gt;New domain&lt;/el-button&gt;\n    &lt;el-button @click=\&quot;resetForm('dynamicValidateForm')\&quot;&gt;Reset&lt;/el-button&gt;\n  &lt;/el-form-item&gt;\n&lt;/el-form&gt;\n\n&quot;,&quot;script&quot;:&quot;\n  export default {\n    data() {\n      return {\n        dynamicValidateForm: {\n          domains: [{\n            key: 1,\n            value: ''\n          }],\n          email: ''\n        }\n      };\n    },\n    methods: {\n      submitForm(formName) {\n        this.$refs[formName].validate((valid) =&gt; {\n          if (valid) {\n            alert('submit!');\n          } else {\n            console.log('error submit!!');\n            return false;\n          }\n        });\n      },\n      resetForm(formName) {\n        this.$refs[formName].resetFields();\n      },\n      removeDomain(item) {\n        var index = this.dynamicValidateForm.domains.indexOf(item);\n        if (index !== -1) {\n          this.dynamicValidateForm.domains.splice(index, 1);\n        }\n      },\n      addDomain() {\n        this.dynamicValidateForm.domains.push({\n          key: Date.now(),\n          value: ''\n        });\n      }\n    }\n  }\n&quot;,&quot;style&quot;:null}">
                    <div class="source" slot="source"><el-form :model="dynamicValidateForm" ref="dynamicValidateForm" label-width="120px" class="demo-dynamic">
  <el-form-item prop="email" label="Email" :rules="[
      { required: true, message: 'Please input email address', trigger: 'blur' },
      { type: 'email', message: 'Please input correct email address', trigger: 'blur,change' }
    ]">
    <el-input v-model="dynamicValidateForm.email"></el-input>
  </el-form-item>
  <el-form-item v-for="(domain, index) in dynamicValidateForm.domains" :label="'Domain' + index" :key="domain.key" :prop="'domains.' + index + '.value'" :rules="{
      required: true, message: 'domain can not be null', trigger: 'blur'
    }">
    <el-input v-model="domain.value"></el-input><el-button @click.prevent="removeDomain(domain)">Delete</el-button>
  </el-form-item>
  <el-form-item>
    <el-button type="primary" @click="submitForm('dynamicValidateForm')">Submit</el-button>
    <el-button @click="addDomain">New domain</el-button>
    <el-button @click="resetForm('dynamicValidateForm')">Reset</el-button>
  </el-form-item>
</el-form>

</div>
