<demo-block class="demo-box" :jsfiddle="{&quot;html&quot;:&quot;&lt;el-autocomplete popper-class=\&quot;my-autocomplete\&quot; v-model=\&quot;state3\&quot; :fetch-suggestions=\&quot;querySearch\&quot; custom-item=\&quot;my-item-en\&quot; placeholder=\&quot;Please input\&quot; @select=\&quot;handleSelect\&quot; icon=\&quot;edit\&quot; :on-icon-click=\&quot;handleIconClick\&quot;&gt;&lt;/el-autocomplete&gt;\n\n\n\n\n&quot;,&quot;script&quot;:&quot;\n  Vue.component('my-item-en', {\n    functional: true,\n    render: function (h, ctx) {\n      var item = ctx.props.item;\n      return h('li', ctx.data, [\n        h('div', { attrs: { class: 'value' } }, [item.value]),\n        h('span', { attrs: { class: 'link' } }, [item.link])\n      ]);\n    },\n    props: {\n      item: { type: Object, required: true }\n    }\n  });\n  export default {\n    data() {\n      return {\n        links: [],\n        state3: ''\n      };\n    },\n    methods: {\n      querySearch(queryString, cb) {\n        var links = this.links;\n        var results = queryString ? link.filter(this.createFilter(queryString)) : links;\n        // call callback function to return recommended data\n        cb(results);\n      },\n      createFilter(queryString) {\n        return (link) =&gt; {\n          return (link.value.indexOf(queryString.toLowerCase()) === 0);\n        };\n      },\n      loadAll() {\n        return [\n          { \&quot;value\&quot;: \&quot;vue\&quot;, \&quot;link\&quot;: \&quot;https://github.com/vuejs/vue\&quot; },\n          { \&quot;value\&quot;: \&quot;element\&quot;, \&quot;link\&quot;: \&quot;https://github.com/ElemeFE/element\&quot; },\n          { \&quot;value\&quot;: \&quot;cooking\&quot;, \&quot;link\&quot;: \&quot;https://github.com/ElemeFE/cooking\&quot; },\n          { \&quot;value\&quot;: \&quot;mint-ui\&quot;, \&quot;link\&quot;: \&quot;https://github.com/ElemeFE/mint-ui\&quot; },\n          { \&quot;value\&quot;: \&quot;vuex\&quot;, \&quot;link\&quot;: \&quot;https://github.com/vuejs/vuex\&quot; },\n          { \&quot;value\&quot;: \&quot;vue-router\&quot;, \&quot;link\&quot;: \&quot;https://github.com/vuejs/vue-router\&quot; },\n          { \&quot;value\&quot;: \&quot;babel\&quot;, \&quot;link\&quot;: \&quot;https://github.com/babel/babel\&quot; }\n         ];\n      },\n      handleSelect(item) {\n        console.log(item);\n      },\n      handleIconClick(ev) {\n        console.log(ev);\n      }\n    },\n    mounted() {\n      this.links = this.loadAll();\n    }\n  }\n&quot;,&quot;style&quot;:&quot;\n  .my-autocomplete {\n    li {\n      line-height: normal;\n      padding: 7px;\n\n      .value {\n        text-overflow: ellipsis;\n        overflow: hidden;\n      }\n      .link {\n        font-size: 12px;\n        color: #b4b4b4;\n      }\n    }\n  }\n&quot;}">
