<span class="hljs-tag">&lt;<span class="hljs-name">script</span>&gt;</span><span class="javascript">
  <span class="hljs-keyword">export</span> <span class="hljs-keyword">default</span> {
    data() {
      <span class="hljs-keyword">return</span> {
        <span class="hljs-attr">links</span>: [],
        <span class="hljs-attr">state4</span>: <span class="hljs-string">''</span>,
        <span class="hljs-attr">timeout</span>:  <span class="hljs-literal">null</span>
      };
    },
    <span class="hljs-attr">methods</span>: {
      loadAll() {
        <span class="hljs-keyword">return</span> [
          { <span class="hljs-string">"value"</span>: <span class="hljs-string">"vue"</span>, <span class="hljs-string">"link"</span>: <span class="hljs-string">"https://github.com/vuejs/vue"</span> },
          { <span class="hljs-string">"value"</span>: <span class="hljs-string">"element"</span>, <span class="hljs-string">"link"</span>: <span class="hljs-string">"https://github.com/ElemeFE/element"</span> },
          { <span class="hljs-string">"value"</span>: <span class="hljs-string">"cooking"</span>, <span class="hljs-string">"link"</span>: <span class="hljs-string">"https://github.com/ElemeFE/cooking"</span> },
          { <span class="hljs-string">"value"</span>: <span class="hljs-string">"mint-ui"</span>, <span class="hljs-string">"link"</span>: <span class="hljs-string">"https://github.com/ElemeFE/mint-ui"</span> },
          { <span class="hljs-string">"value"</span>: <span class="hljs-string">"vuex"</span>, <span class="hljs-string">"link"</span>: <span class="hljs-string">"https://github.com/vuejs/vuex"</span> },
          { <span class="hljs-string">"value"</span>: <span class="hljs-string">"vue-router"</span>, <span class="hljs-string">"link"</span>: <span class="hljs-string">"https://github.com/vuejs/vue-router"</span> },
          { <span class="hljs-string">"value"</span>: <span class="hljs-string">"babel"</span>, <span class="hljs-string">"link"</span>: <span class="hljs-string">"https://github.com/babel/babel"</span> }
         ];
      },
      querySearchAsync(queryString, cb) {
        <span class="hljs-keyword">var</span> links = <span class="hljs-keyword">this</span>.links;
        <span class="hljs-keyword">var</span> results = queryString ? links.filter(<span class="hljs-keyword">this</span>.createFilter(queryString)) : links;

        clearTimeout(<span class="hljs-keyword">this</span>.timeout);
