          <span class="hljs-tag">&lt;<span class="hljs-name">el-menu-item</span> <span class="hljs-attr">index</span>=<span class="hljs-string">"1-2"</span>&gt;</span>item one<span class="hljs-tag">&lt;/<span class="hljs-name">el-menu-item</span>&gt;</span>
