        <span class="hljs-attr">loading</span>: <span class="hljs-literal">false</span>,
        <span class="hljs-attr">states</span>: [<span class="hljs-string">"Alabama"</span>, <span class="hljs-string">"Alaska"</span>, <span class="hljs-string">"Arizona"</span>,
        <span class="hljs-string">"Arkansas"</span>, <span class="hljs-string">"California"</span>, <span class="hljs-string">"Colorado"</span>,
