<demo-block class="demo-box" :jsfiddle="{&quot;html&quot;:&quot;&lt;template&gt;\n  &lt;el-table :data=\&quot;tableData\&quot; border style=\&quot;width: 100%\&quot;&gt;\n    &lt;el-table-column prop=\&quot;date\&quot; label=\&quot;Date\&quot; sortable width=\&quot;180\&quot;&gt;\n    &lt;/el-table-column&gt;\n    &lt;el-table-column prop=\&quot;name\&quot; label=\&quot;Name\&quot; width=\&quot;180\&quot;&gt;\n    &lt;/el-table-column&gt;\n    &lt;el-table-column prop=\&quot;address\&quot; label=\&quot;Address\&quot; :formatter=\&quot;formatter\&quot;&gt;\n    &lt;/el-table-column&gt;\n    &lt;el-table-column prop=\&quot;tag\&quot; label=\&quot;Tag\&quot; width=\&quot;100\&quot; :filters=\&quot;[{ text: 'Home', value: 'Home' }, { text: 'Office', value: 'Office' }]\&quot; :filter-method=\&quot;filterTag\&quot; filter-placement=\&quot;bottom-end\&quot;&gt;\n      &lt;template scope=\&quot;scope\&quot;&gt;\n        &lt;el-tag :type=\&quot;scope.row.tag === 'Home' ? 'primary' : 'success'\&quot; close-transition&gt;{{scope.row.tag}}&lt;/el-tag&gt;\n      &lt;/template&gt;\n    &lt;/el-table-column&gt;\n  &lt;/el-table&gt;\n&lt;/template&gt;\n\n\n&quot;,&quot;script&quot;:&quot;\n  export default {\n    data() {\n      return {\n        tableData: [{\n          date: '2016-05-03',\n          name: 'Tom',\n          address: 'No. 189, Grove St, Los Angeles',\n          tag: 'Home'\n        }, {\n          date: '2016-05-02',\n          name: 'Tom',\n          address: 'No. 189, Grove St, Los Angeles',\n          tag: 'Office'\n        }, {\n          date: '2016-05-04',\n          name: 'Tom',\n          address: 'No. 189, Grove St, Los Angeles',\n          tag: 'Home'\n        }, {\n          date: '2016-05-01',\n          name: 'Tom',\n          address: 'No. 189, Grove St, Los Angeles',\n          tag: 'Office'\n        }]\n      }\n    },\n    methods: {\n      formatter(row, column) {\n        return row.address;\n      },\n      filterTag(value, row) {\n        return row.tag === value;\n      }\n    }\n  }\n&quot;,&quot;style&quot;:null}">
