    <span class="hljs-tag">&lt;<span class="hljs-name">el-tooltip</span> <span class="hljs-attr">class</span>=<span class="hljs-string">"item"</span> <span class="hljs-attr">effect</span>=<span class="hljs-string">"dark"</span> <span class="hljs-attr">content</span>=<span class="hljs-string">"Top Right prompts info"</span> <span class="hljs-attr">placement</span>=<span class="hljs-string">"top-end"</span>&gt;</span>
