      <span class="hljs-tag">&lt;<span class="hljs-name">el-button</span>&gt;</span>left-start<span class="hljs-tag">&lt;/<span class="hljs-name">el-button</span>&gt;</span>
    <span class="hljs-tag">&lt;/<span class="hljs-name">el-tooltip</span>&gt;</span>
    <span class="hljs-tag">&lt;<span class="hljs-name">el-tooltip</span> <span class="hljs-attr">class</span>=<span class="hljs-string">"item"</span> <span class="hljs-attr">effect</span>=<span class="hljs-string">"dark"</span> <span class="hljs-attr">content</span>=<span class="hljs-string">"Left Center prompts info"</span> <span class="hljs-attr">placement</span>=<span class="hljs-string">"left"</span>&gt;</span>
      <span class="hljs-tag">&lt;<span class="hljs-name">el-button</span>&gt;</span>left<span class="hljs-tag">&lt;/<span class="hljs-name">el-button</span>&gt;</span>
