    <span class="hljs-attribute">box-sizing</span>: border-box;
    <span class="hljs-attribute">margin-right</span>: <span class="hljs-number">20px</span>;
  }
</span><span class="hljs-tag">&lt;/<span class="hljs-name">style</span>&gt;</span>
</code></pre>
</div></demo-block>
<h3 id="on-demand"><a class="header-anchor" href="#on-demand" aria-hidden="true">¶</a> On demand</h3>
<pre><code class="hljs language-js"><span class="hljs-comment">// fade/zoom</span>
<span class="hljs-keyword">import</span> <span class="hljs-string">'element-ui/lib/theme-defaut/base.css'</span>;
<span class="hljs-comment">// collapse</span>
<span class="hljs-keyword">import</span> CollapseTransition <span class="hljs-keyword">from</span> <span class="hljs-string">'element-ui/lib/transitions/collapse-transition'</span>;
<span class="hljs-keyword">import</span> Vue <span class="hljs-keyword">from</span> <span class="hljs-string">'vue'</span>

Vue.component(CollapseTransition.name, CollapseTransition)
</code></pre>


</section></template>
<style>
  .transition-box {
    margin-bottom: 10px;
    width: 200px;
    height: 100px;
    border-radius: 4px;
    background-color: #20A0FF;
    text-align: center;
    color: #fff;
    padding: 40px 20px;
    margin-right: 20px;
    box-sizing: border-box;
  }
</style>
<script>
