tton size=\&quot;mini\&quot; on-click={ () =&gt; this.remove(store, data) }&gt;Delete&lt;/el-button&gt;\n            &lt;/span&gt;\n          &lt;/span&gt;);\n      }\n    }\n  };\n&quot;,&quot;style&quot;:null}">
